INSERT INTO `events` (`id`, `kind`, `title`, `subtitle`, `information`, `speaker_name`, `speaker_title`, `speaker_img`, `front_image`, `event_date`, `location`, `slider`, `detail`, `is_show`, `created_at`, `updated_at`, `deleted_at`) VALUES (6, 2, 'Digital Learning Bootcamp', 'Develop \"Fit, Fast & Fun\" Digital Learning', 'Apakah perusahaaan Anda mengalami kesulitan dalam menerapkan Digital Learning ? Atau hasilnya kurang memuaskan ?', 'Anna Maria Ph.D', 'Praktisi Corpu dan Corporate Learning', 'arpBmVN6Xnk4YlqL1Sx4c11UYQFgpgjyin2XqXoe.jpg', 'XN9Yb9HeRWXEQ0qL6KJwhmU6ZLOW4hQgZBQbQwkd.jpg', '2023-11-14 09:00:00', 'Hotel Santika Premier Slipi, Jl. KS Tubun no. 7, Jakarta Pusat', '[\"xTR3ujy2VeFXTU58ckZB9a1ZLemgYaoOIXKyhVIH.jpg\"]', '<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: verdana, geneva, sans-serif;\"><strong><span lang=\"EN-US\" style=\"font-size: 14.0pt;\">DEVELOP &ldquo;FIT, FAST, and FUN&rdquo; DIGITAL LEARNING</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: verdana, geneva, sans-serif;\"><strong><span lang=\"EN-US\" style=\"font-size: 14.0pt;\">14 - 15 NOVEMBER 2023</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: verdana, geneva, sans-serif;\"><strong><span lang=\"EN-US\">Apakah Digital Learning yang dibuat mengalami hal-hal berikut ini:</span></strong></span></p>\r\n<ul>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Kurang mendapatkan peminat dari pegawai, karena kurang engaging</span></li>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Kurang mendapatkan dukungan dari manajemen karena dinilai belum memberikan dampak pembelajaran ke kinerja organisasi</span></li>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Kurang sesuai dengan kebutuhan pembelajaran unit kerja</span></li>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Lama dan berbiaya mahal dalam membuatnya</span></li>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Kurang variatif dalam metode pembelajarannya</span></li>\r\n<li class=\"MsoNormal\" style=\"text-align: justify; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\">Tidak komprehensif karena belum adanya Roadmap Transformasi Digital Learning, yang mendapatkan dukungan manajemen</span></li>\r\n</ul>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 12pt;\"><strong><span lang=\"EN-US\" style=\"font-family: verdana, geneva, sans-serif;\">Digital Learning Bootcamp akan membantu Anda menyelesaikan masalah tersebut!</span></strong></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\">Tujuan Digital Bootcamp ini adalah memberikan pembekalaan kepada peserta untuk dapat melakukan transformasi digital learning di organasi secara cepat dan menggunakan kaedah best-practice internasional.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\" style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\">Setelah mengikuti Digital Learning Bootcamp ini, peserta akan mampu:</span></p>\r\n<ul style=\"list-style-type: square;\">\r\n<li class=\"MsoListParagraphCxSpFirst\" style=\"text-indent: -0.25in; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"mso-ascii-font-family: Calibri; mso-fareast-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: Calibri;\"><span style=\"mso-list: Ignore;\"><span style=\"font-style: normal; font-variant: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-weight: normal; font-stretch: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span><!--[endif]--><span lang=\"EN-US\">Memahami terkait dengann Digital Learning Principles</span></span></li>\r\n<li class=\"MsoListParagraphCxSpMiddle\" style=\"text-indent: -0.25in; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"mso-ascii-font-family: Calibri; mso-fareast-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: Calibri;\"><span style=\"mso-list: Ignore;\"><span style=\"font-style: normal; font-variant: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-weight: normal; font-stretch: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span><!--[endif]--><span lang=\"EN-US\">Menyusun materi pembelajaran digital yang interaktif dan mengikuti kaeadah Instructional Design</span></span></li>\r\n<li class=\"MsoListParagraphCxSpMiddle\" style=\"text-indent: -0.25in; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"mso-ascii-font-family: Calibri; mso-fareast-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: Calibri;\"><span style=\"mso-list: Ignore;\"><span style=\"font-style: normal; font-variant: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-weight: normal; font-stretch: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span><!--[endif]--><span lang=\"EN-US\">Memahami komponen dalam Learning Management System / Platform</span></span></li>\r\n<li class=\"MsoListParagraphCxSpLast\" style=\"text-indent: -0.25in; font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\"><span lang=\"EN-US\" style=\"mso-ascii-font-family: Calibri; mso-fareast-font-family: Calibri; mso-hansi-font-family: Calibri; mso-bidi-font-family: Calibri;\"><span style=\"mso-list: Ignore;\"><span style=\"font-style: normal; font-variant: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-weight: normal; font-stretch: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span><!--[endif]--><span lang=\"EN-US\">Membuat Digital Learning Roadmap untuk transformasi Digital Learning di Organisasi</span></span></li>\r\n</ul>\r\n<p><span lang=\"EN-US\" style=\"font-family: verdana, geneva, sans-serif; font-size: 10pt;\">Narasumber yang Expert dalam bidang Digital Learning akan hadir dalam acara ini adalah:</span></p>\r\n<ul>\r\n<li style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\"><span lang=\"EN-US\">Joseph Goh</span><span lang=\"EN-US\">&ndash; Digital Learning Platform Specialist from Singapore</span></span></li>\r\n<li style=\"font-size: 10pt; font-family: verdana, geneva, sans-serif;\"><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: verdana, geneva, sans-