INSERT INTO `news` (`id`, `user_id`, `title`, `subtitle`, `front_image`, `slider`, `detail`, `is_show`, `created_at`, `updated_at`, `deleted_at`) VALUES (5, 2, 'Learning Bootcamp @BPSDM Jawa Barat', 'Wahana Tatar together with BPSDM Jawa Barat and PT Askrindo organized a Learning Bootcamp event at Cimahi, gathering people that involve in corporate university and learning center', 'xvWX51j0QG2LrBbvGadrBjLFDQtEKBheX4jK3fEF.png', '[\"9fai4Zprg5sNAVmHdZ60JxCY2BWKMCKagk33XknE.png\", \"LzGgCHjMoLroJ2czdhdRLEz74eDSbeDXw738tS9D.png\", \"7XzquPZhH6M2hZ4AYYbUTDNNCVq8IQll0zXjoo5b.png\", \"HvIW6j9x0kIyCC7BvFlepSx5S2XeOY1Sv2wgRUtq.png\"]', '<p><strong><span style=\"font-size: 18pt;\">Learning Boorcamp 2023 @Cimahi, a starting point for people who concern on developing Next Gen CorpU</span></strong></p>\r\n<p><span style=\"font-size: 14pt;\">Learning Boorcamp is a new series of gathering event, which involve people from several organization practicing corporate university and learning center. This event is a part of Corporate University Summit that has been organized by PT Wahana Tatar Wirakelola (WTW) that already run in 6 series during last six years.</span></p>\r\n<p><span style=\"font-size: 14pt;\">This event was done on 1 - 2 August 2023 and attended by more than 120 people from various organization.&nbsp; The speakers of this event are practician of Learning &amp; People Development who has experienced the best practice of Corporate University :</span></p>\r\n<ul>\r\n<li><span style=\"font-size: 14pt;\">Dr. Hery Antasari - Head of BPSDM Jawa Barat</span></li>\r\n<li><span style=\"font-size: 14pt;\">Bambang Juli Iswanto - Head of Learning @BPPK - CorpU Ministry of Finance&nbsp; Indonesia</span></li>\r\n<li><span style=\"font-size: 14pt;\">Dr Ir Mustanginah - Head of CorpU Ministry of Research, Education and Culture Indonesia</span></li>\r\n<li><span style=\"font-size: 14pt;\">Rio Adrianto ST MBA - Head of CorpU PT PLN</span></li>\r\n<li><span style=\"font-size: 14pt;\">Sandri Justiana - Learning Specialist @ KPK</span></li>\r\n<li><span style=\"font-size: 14pt;\">Joseph Goh - DialogEDU Singapore</span></li>\r\n<li><span style=\"font-size: 14pt;\">Kun Wahyu Wardana - Director of PT Askrindo</span></li>\r\n<li><span style=\"font-size: 14pt;\">Anna Maria Ph.D. - Director of PT WTW, also CorpU Expert</span></li>\r\n</ul>\r\n<p>&nbsp;</p>', 1, '2023-08-12 03:52:32', '2023-08-16 03:32:24', NULL);
INSERT INTO `news` (`id`, `user_id`, `title`, `subtitle`, `front_image`, `slider`, `detail`, `is_show`, `created_at`, `updated_at`, `deleted_at`) VALUES (7, 2, 'Develop Fit, Fast, and Fun Digital Learning', 'Digital Learning Bootcamp', 'uR7WYYYTM42aXilJ1iO7eQYYGWIChl4Dtt9tHO36.png', '[\"bgbXRGDimg0QvhTBFe1jqQGtneTFFKJEvJDILgn4.jpg\", \"4YaYBlgNEPM38DHfW4LOPKNtQ1LXMnOYMnvBoFpk.jpg\", \"5RNv2tICTNKHzOsFBkHOxdJAs4NVXGvOAHQSQM3d.jpg\", \"r3bySk0jyL2NvHleG2yrAJokAQuCiGxt87w44mTt.jpg\", \"gaNKpRecXv4KGdsVhQ42a1MUjxdzsW58LcddUrOB.jpg\", \"XYUwwA3buHnILpXeRqyPpksC47SeQJ5A6gvNoD7W.jpg\", \"ZMtXf209bJgqINBNzHm7Hge1LqTp2py8SzbcVcBG.jpg\", \"9Ne6ID9druoIq7gFuHxILBx3KLv4S3eGhPyfl98G.jpg\"]', '<p class=\"MsoNormal\">The Digital Learning Bootcamp event with the theme Develop Fit, Fast, and Fun Digital Learning which was held on 14 - 15 November 2023 at the Santika Premiere Hotel Slipi Jakarta, was a great success attracting the attention and enthusiasm of the participants. This event, organized by PT Wahana Tatar Wirakelola, is a magnet for those who want to hone their skills and gain a deep understanding of the digital world.</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><strong>Participants from Various Organizations</strong></p>\r\n<p class=\"MsoNormal\">With participants coming from various organizations, ranging from ministries, state-owned companies, local governments and private companies, this event provides an inclusive platform for everyone who wants to improve their digital competence. Through a series of carefully designed learning sessions, participants get the opportunity to explore digital learning</p>\r\n<p class=\"MsoNormal\"><strong>Expert Speaker in the Field</strong></p>\r\n<p class=\"MsoNormal\">This event was also attended by a number of expert speakers who have extensive experience in the digital industry. They share their knowledge practically and provide in-depth insights into the latest trends and opportunities in the digital world. The participants were very enthusiastic about asking questions and discussing directly with the speakers.</p>\r\n<p class=\"MsoNormal\"><strong>Digital Event Format Innovation</strong></p>\r\n<p class=\"MsoNormal\">By utilizing digital technology, this event presents an interactive and dynamic learning experience. Learning sessions take place in an easily accessible online format, allowing participants to take part in events without geographic restrictions. The materials presented can also be accessed on-demand, providing additional flexibility for participants.</p>\r\n<p class=\"MsoNormal\"><strong>Networking and Collaboration Opportunities</strong></p>\r\n<p class=\"MsoNormal\">Apart from being a learning event, this event is also an ideal place for networking and collaboration. Attendees can interact with fellow attendees, speakers, and event sponsors to share experiences, build connections, and even explore future collaboration opportunities.</p>\r\n<p class=\"MsoNormal\"><strong>Positive Response from Participants</strong></p>\r\n<p class=\"MsoNormal\">With the enthusiasm of the participants and the positive response received, it is hoped that this Digital Learning Bootcamp event can become a reference for similar activities in the future. The organizers are committed to continuing to present digital educational events that are useful and relevant to technological developments.</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">The Digital Learning Bootcamp event not only improves participants\' skills in the digital field, but also becomes a forum for exchanging ideas and inspiration. It is hoped that the success of this event will provide a positive impetus for the development of human resources in the digital era.</p>', 1, '2023-11-21 04:19:02', '2023-11-21 04:19:02', NULL);
